import React, { useState } from "react";

function App() {
  const [recipeData, setRecipeData] = useState([{}]);
  const [searchQuery, setSearchQuery] = useState("");

  const randomIndex = Math.floor(Math.random() * 100);

  function handleSearch(event) {
    event.preventDefault(); // prevents default form submission behaviour
    fetch("/get-recipe", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ query: searchQuery }),
    })
      .then((response) => response.json())
      .then((data) => {
        setRecipeData(data);
        console.log(data);
      });
  }

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchQuery}
          onChange={(event) => setSearchQuery(event.target.value)}
          placeholder="Enter the type of food you want a recipe for..."
        />
        <button type="submit">Search</button>
      </form>
      {typeof recipeData.number === "undefined" ? (
        <p>No recipe yet...</p>
      ) : (
        <div>
          <p>{recipeData.results[randomIndex].title}</p>
          <img src={recipeData.results[randomIndex].image} alt="Lovely food" />
        </div>
      )}
    </div>
  );
}

export default App;
